INSERT INTO public.use_cases VALUES ('e221505d-3226-4df7-9b4b-cd4691197ce6', 'Manual Back-and-Forth for Claims Adjustors with Underwriters to Confirm Coverage', 'GenAI-powered policy comparison engine to analyze inception, renewal, and endorsement documents
