INSERT INTO public.use_cases VALUES ('3e902b90-c3cd-4fc2-a541-d46f2aadb918', 'AI-Driven Risk Monitoring and Early Warning System', 'This AI use case involves continuously monitoring insurance portfolios using machine learning and predictive analytics to identify emerging risks, unusual claim activity, or complex exposures that require early intervention despite their low immediate value. The AI system analyzes structured and unstructured data from multiple sources—claims, underwriting, external events, social signals—to detect patterns, anomalies, and trends that may predict future adverse outcomes or escalating risk profiles.', NULL, 2, 2, 4, 3, 5, 4, 4, 4, 2.6, 4.2, 'Watchlist', '2025-09-03 17:12:22.84126', 2, 4, '{"Specialty (E&S)","All Lines (CL,SL,Others)"}', '{"Risk Consulting","Regulatory & Compliance"}', '{"Risk Monitoring","Risk Evaluation"}', '{"All Segments"}', '{UK,Ireland}', NULL, 'true', 'true', 'active', '2025-09-03 17:12:22.84126', NULL, 'industry_standard', 'Selected', NULL, NULL, NULL, NULL, 'Insurers face difficulty in proactively identifying latent or evolving risks within large volumes of low-value policies or claims that are complex in nature. Traditional systems may overlook subtle signs of potential loss development, fraud, regulatory issues, or operational inefficiencies, leading to surprise financial impacts, increased reserves, delayed interventions, and reduced profitability.', 'Risk Services', 'Backlog', NULL, NULL, NULL, NULL, NULL, NULL, '{"Machine Learning","Predictive Analytics"}', '{"Claims Database","Broker Data & Feeds","Policy Database","Customer Database"}', '{"Risk Management",Compliance,"External Partners"}', 'high', 3, 4, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'true', 'false', 'true', 'true', NULL, NULL, NULL, NULL, 'false', NULL, NULL, 'HEX_INT_032', 'false', NULL, 'M', 111375, 222750, 8, 16, '3-6');
INSERT INTO public.use_cases VALUES ('3a67b666-4814-4f3a-b4ee-62cfef146a41', 'Enter-once automation for underwriter data  (Straight through processing)', 'AI-enabled automation / RPA for “enter once, feed many” workflow across core systems. Intelligent validation to ensure one source of truth flows across raters, PAS, doc gen, and renewal triggers.', 'Process Automation', 3, 4, 4, 4, 3, 3, 3, 2, 3.6, 2.6, 'Quick Win', '2025-09-03 17:12:34.971872', 3, 2, '{"All Lines (CL",SL,Others),"All Lines (CL,SL,Others)"}', '{"Underwriting & Triage"}', NULL, '{"All Segments"}', '{UK}', NULL, 'true', 'true', 'active', '2025-09-03 17:12:34.971872', NULL, 'rsa_internal', 'Good use case for immediate implementation', NULL, NULL, NULL, NULL, 'Underwriters must re-enter the same data across multiple disconnected systems (Dynamics, Excel raters, PAS/UKRIS, AIS, doc gen, Thunderhead, Profhin). Manual copying of outputs (raters → doc gen → PAS) adds rework, creates inconsistencies, and forces email/Excel workarounds. Leads to high error risk, wasted effort, compliance gaps, and downstream ops rework.', 'Helen Franklin; supported by Iain Cameron; Craig Brownrigg (Ops) flagged downstream impacts.', 'Discovery', 'Dynamics, Excel raters, UKRIS, AIS, Thunderhead, Profin (plus MSD renewal triggers – related overlap).', 'immediate', 'Significant efficiency gains; fewer errors; faster turnaround; improved compliance; reduced downstream ops effort; stronger broker trust from consistent outputs.', 'High', NULL, 'Dynamics, Excel raters, UKRIS, AIS, Thunderhead, Profin (plus MSD renewal triggers – related overlap).', '{"Rule-based Systems"}', '{"Policy Database","Customer Database","Submissions & Underwriting Database"}', '{"Underwriting Teams",IT/Technology}', 'low', 3, 3, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'false', 'false', 'false', 'true', NULL, NULL, 'Automation Opportunities CL.pptx', '2025-09-03 10:07:57.36', 'false', 'e8953e9c-c30f-44dc-a