INSERT INTO public.use_cases VALUES ('bd31557b-d22e-47f8-b9ae-a5759dc3d5f5', 'Claims Triage by Value Thresholds', 'AI-based triage engine leveraging claim attributes beyond value (LOB, complexity, completeness of documentation, claimant profile, geography)
